#name SheeranXPedal5
#sustain
#repeat

if fmode equals 0{
  case mode {
      :Reset {TransportStart Record}
      :Record Overdub
      :Overdub Play
      :Play Overdub
      :Mute Play
      else print("Unhandled mode" mode)
  }
}
else if fmenuTap{
  TransportTap
}

function OnSustain(sustainCount sustainElapsed) {
     if fmode equals 0 Undo   
}

SheeranXPedal6.msl
#name SheeranXPedal6
#sustain

if fmode equals 0{
  in all {MuteOn}
}

function OnSustain() {
  in all {if fmode equals 0 {Reset}}
}

SheeranXPedal7.msl
#name SheeranXPedal7

if mutemode equals 0{
  mutemode = 1
}
else{
  mutemode = 0
}

SheeranXPedal8.msl
#name SheeranXPedal8
#sustain
#repeat

if fmode equals 0{
  fmode = 1
  fmenuA = 1
  fmenuB = 0
  fmenuTap = 0
}else{
  fmode = 0
  fmenuA = 0
  fmenuB = 0
  fmenuTap = 0
}

function OnSustain(sustainCount){
  if fmode equals 2{
    fmode = 1
    fmenuA = 1
    fmenuB = 0
    fmenuTap = 0     
  }else{
    fmode = 2
    fmenuA = 0
    fmenuB = 1
    fmenuTap = 0
  }
}

function OnRepeat(repeatCount){
  fmode = 3
  fmenuA = 0
  fmenuB = 0
  fmenuTap = 1
}

