#sustain 500
#repeat 500

if normalmode equals 1{
  //We must be in Normal Mode so pressing pedal 8 enters Function Menu A
  fmenuA = 1
  normalmode = 0
}
else{
  //We must not be in Normal Mode so pressing pedal 8 exits and returns to Normal Mode
  normalmode = 1
  fmenuA = 0
}
